/// <summary>
/// On update send the business event
/// </summary>
/// <param name="sender"></param>
/// <param name="e"></param>
[DataEventHandler(tableStr(DMFDefinitionGroupExecution), DataEventType::Updated)]
public static void DMFDefinitionGroupExecution_onUpdated(Common sender, DataEventArgs e)
{
    DMFDefinitionGroupExecution dmfDefinitionGroupExecution = sender;
    DMFDefinitionGroup dmfDefinitionGroup = DMFDefinitionGroup::find(dmfDefinitionGroupExecution.DefinitionGroup);
    if (dmfDefinitionGroupExecution &&
        dmfDefinitionGroup.OperationType == DMFOperationType::Import &&
        dmfDefinitionGroup.ProjectCategory == DMFProjectCategory::Integration
        )
    {
        NAVAXDMFImportStatusBusinessEvent businessEvent = NAVAXDMFImportStatusBusinessEvent::newFromDMFDefinitionGroupExecutionRecId(dmfDefinitionGroupExecution.RecId);
        businessEvent.send();
    }
}
